import React from 'react';
import { motion } from 'framer-motion';
import { Star, Quote } from 'lucide-react';

const SocialProof: React.FC = () => {
  const testimonials = [
    {
      name: "김민수",
      role: "게스트하우스 운영자",
      location: "제주도",
      rating: 5,
      content: "FineHost 덕분에 하루 3시간씩 절약할 수 있었어요. 이제 다른 일에도 시간을 투자할 수 있게 되었습니다.",
      avatar: "👨‍💼"
    },
    {
      name: "이지영",
      role: "펜션 운영자",
      location: "강원도",
      rating: 5,
      content: "가격 최적화 기능이 정말 대박이에요! 이번 달 매출이 25% 증가했어요. 정말 감사합니다.",
      avatar: "👩‍💼"
    },
    {
      name: "박준호",
      role: "소형호텔 운영자",
      location: "부산",
      rating: 5,
      content: "여러 OTA 사이트를 일일이 확인하던 게 가장 힘들었는데, 이제 한 번에 다 관리할 수 있어요.",
      avatar: "👨‍💼"
    }
  ];

  const stats = [
    { number: "500+", label: "활성 사용자" },
    { number: "20%", label: "평균 매출 증가" },
    { number: "3시간", label: "일일 시간 절약" },
    { number: "4.9", label: "평균 평점" }
  ];

  return (
    <section className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Stats Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
            이미 많은 분들이 신뢰하고 있습니다
          </h2>
          <p className="text-xl text-gray-600 mb-12">
            실제 사용자들의 생생한 후기를 확인해보세요
          </p>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="text-center"
              >
                <div className="text-3xl md:text-4xl font-bold text-primary-600 mb-2">
                  {stat.number}
                </div>
                <div className="text-gray-600">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Testimonials */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          viewport={{ once: true }}
          className="grid grid-cols-1 md:grid-cols-3 gap-8"
        >
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
              viewport={{ once: true }}
              className="bg-white rounded-2xl p-8 shadow-lg relative"
            >
              {/* Quote Icon */}
              <div className="absolute top-4 right-4 text-gray-200">
                <Quote size={24} />
              </div>

              {/* Rating */}
              <div className="flex items-center mb-4">
                {[...Array(testimonial.rating)].map((_, i) => (
                  <Star key={i} className="w-5 h-5 text-yellow-400 fill-current" />
                ))}
              </div>

              {/* Content */}
              <p className="text-gray-700 mb-6 leading-relaxed">
                "{testimonial.content}"
              </p>

              {/* Author */}
              <div className="flex items-center">
                <div className="text-2xl mr-4">{testimonial.avatar}</div>
                <div>
                  <div className="font-semibold text-gray-900">{testimonial.name}</div>
                  <div className="text-sm text-gray-600">{testimonial.role}</div>
                  <div className="text-sm text-gray-500">{testimonial.location}</div>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Trust Indicators */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.6 }}
          viewport={{ once: true }}
          className="text-center mt-16 pt-12 border-t border-gray-200"
        >
          <h3 className="text-2xl font-bold text-gray-900 mb-8">
            안전하고 신뢰할 수 있는 서비스
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">🔒</span>
              </div>
              <h4 className="font-semibold text-gray-900 mb-2">데이터 보안</h4>
              <p className="text-gray-600 text-sm">
                모든 데이터는 암호화되어 안전하게 보관됩니다
              </p>
            </div>

            <div className="text-center">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">💬</span>
              </div>
              <h4 className="font-semibold text-gray-900 mb-2">24/7 지원</h4>
              <p className="text-gray-600 text-sm">
                언제든지 전문가의 도움을 받을 수 있습니다
              </p>
            </div>

            <div className="text-center">
              <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">💰</span>
              </div>
              <h4 className="font-semibold text-gray-900 mb-2">30일 무료 체험</h4>
              <p className="text-gray-600 text-sm">
                부담 없이 30일간 모든 기능을 체험해보세요
              </p>
            </div>
          </div>
        </motion.div>

        {/* CTA */}
