  {
    name: 'ÍπÄÏßÄÌòÑ',
    role: 'ÎåÄÌëú / Product Owner',
    intro: 'ÏàôÎ∞ïÏóÖÏùò Î™®Îì† Í≥†ÎØºÏùÑ ITÎ°ú Ìï¥Í≤∞ÌïòÍ≥† Ïã∂Ïñ¥Ïöî.',
    avatar: 'üßë‚Äçüíº',
  },
  {
    name: 'Ïù¥ÏàòÎØº',
    role: 'ÌîÑÎ°†Ìä∏ÏóîÎìú Í∞úÎ∞ú',
    intro: 'ÏÇ¨Ïö©ÏûêÏóêÍ≤å Í∞ÄÏû• Ïâ¨Ïö¥ Í≤ΩÌóòÏùÑ ÎßåÎì≠ÎãàÎã§.',
    avatar: 'üë©‚Äçüíª',
  },
  {
    name: 'Î∞ïÏ§ÄÌò∏',
    role: 'Î∞±ÏóîÎìú Í∞úÎ∞ú',
    intro: 'Îç∞Ïù¥ÌÑ∞ÏôÄ ÏûêÎèôÌôîÎ°ú Ïö¥ÏòÅÏùÑ Îçî Ìé∏ÌïòÍ≤å!',
    avatar: 'üë®‚Äçüíª',
  },
  {
    name: 'ÏµúÏú†ÏßÑ',
    role: 'ÎßàÏºÄÌåÖ & Í≥†Í∞ùÏßÄÏõê',
    intro: 'Í≥†Í∞ùÏùò Î™©ÏÜåÎ¶¨Î•º Í∞ÄÏû• Í∞ÄÍπåÏù¥ÏóêÏÑú Îì£ÏäµÎãàÎã§.',
    avatar: 'üßë‚Äçüîß',
  },
];

const Team: React.FC = () => {
  return (
    <section className="py-20 bg-white" id="team">
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-14">
          <motion.h2
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-3xl md:text-4xl font-bold text-gray-900 mb-4"
          >
            ÌååÏù∏Ìò∏Ïä§Ìä∏ ÌåÄÏùÑ ÏÜåÍ∞úÌï©ÎãàÎã§
          </motion.h2>
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
            className="text-lg text-gray-600 max-w-2xl mx-auto"
          >
            "ÌòºÏûê Ïö¥ÏòÅÌï¥ÎèÑ Í¥úÏ∞ÆÎã§"Îäî ÎßàÏùåÏùÑ Ï†ÑÌïòÍ≥† Ïã∂Ïñ¥Ïöî.<br />
            ÌååÏù∏Ìò∏Ïä§Ìä∏ ÌåÄÏùÄ ÏàôÎ∞ïÏóÖÏ£ºÎãòÏùò Îì†Îì†Ìïú ÎèôÎ∞òÏûêÍ∞Ä ÎêòÍ≤†ÏäµÎãàÎã§.
          </motion.p>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8">
          {teamMembers.map((member, idx) => (
            <motion.div
              key={member.name}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.1 * idx }}
              viewport={{ once: true }}
              className="bg-gray-50 rounded-2xl p-8 shadow text-center flex flex-col items-center"
            >
              <div className="text-5xl mb-4">{member.avatar}</div>
              <div className="font-bold text-lg text-gray-900 mb-1">{member.name}</div>
              <div className="text-primary-600 font-medium mb-2">{member.role}</div>
              <div className="text-gray-600 text-sm">{member.intro}</div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Team;
