import React from 'react';
import { motion } from 'framer-motion';
import { Clock, DollarSign, MessageSquare, TrendingDown, Users, Zap } from 'lucide-react';

const Problems: React.FC = () => {
  const problems = [
    {
      icon: Clock,
      title: "시간 낭비",
      description: "여러 OTA 사이트를 일일이 확인하고 방막기하는데 하루 종일 시간을 보내고 있나요?",
      color: "text-red-500"
    },
    {
      icon: DollarSign,
      title: "수익 손실",
      description: "경쟁 숙소 가격을 제대로 파악하지 못해 적정 가격을 못 찾고 있나요?",
      color: "text-orange-500"
    },
    {
      icon: MessageSquare,
      title: "고객 응대 부담",
      description: "체크인 안내, 리뷰 요청 등을 수기로 발송하는데 시간이 부족하나요?",
      color: "text-blue-500"
    },
    {
      icon: TrendingDown,
      title: "매출 분석 어려움",
      description: "전체 매출 흐름과 수익성을 한눈에 파악하기 어려우신가요?",
      color: "text-purple-500"
    },
    {
      icon: Users,
      title: "마케팅 부재",
      description: "SNS 마케팅을 하고 싶어도 어떻게 시작해야 할지 모르시나요?",
      color: "text-green-500"
    },
    {
      icon: Zap,
      title: "운영 복잡성",
      description: "예약부터 정산까지 모든 과정이 복잡하고 체계적이지 않나요?",
      color: "text-indigo-500"
    }
  ];

  return (
    <section id="problems" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <motion.h2
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-3xl md:text-4xl font-bold text-gray-900 mb-4"
          >
            이런 고민들, 혹시 있으신가요?
          </motion.h2>
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
            className="text-xl text-gray-600 max-w-3xl mx-auto"
          >
            개인 숙박업주들이 겪는 공통적인 문제들을 해결해드립니다
          </motion.p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {problems.map((problem, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="card group hover:scale-105 transition-transform duration-200"
            >
              <div className="flex items-start space-x-4">
                <div className={`p-3 rounded-lg bg-gray-50 group-hover:bg-gray-100 transition-colors`}>
                  <problem.icon className={`${problem.color} w-6 h-6`} />
                </div>
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    {problem.title}
                  </h3>
                  <p className="text-gray-600 leading-relaxed">
                    {problem.description}
                  </p>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.6 }}
          viewport={{ once: true }}
          className="text-center mt-16 p-8 bg-gradient-to-r from-primary-50 to-secondary-50 rounded-2xl"
        >
          <h3 className="text-2xl font-bold text-gray-900 mb-4">
            걱정하지 마세요!
          </h3>
          <p className="text-lg text-gray-600 mb-6">
            이런 문제들을 한 번에 해결할 수 있는 솔루션이 있습니다
          </p>
          <button className="btn-primary">
            해결책 보기
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default Problems;
